INSERT INTO `location` (`id_location`, `name_location`, `description_location`, `img_location`) VALUES
(6, 'Long An', '<p><strong>Long An</strong>&nbsp;l&agrave; một&nbsp;tỉnh&nbsp;thuộc v&ugrave;ng&nbsp;Đồng bằng s&ocirc;ng Cửu Long,&nbsp;Việt Nam. Đ&acirc;y l&agrave; địa phương nằm ở cửa ng&otilde; của v&ugrave;ng Đồng bằng s&ocirc;ng Cửu Long, liền kề với&nbsp;Th&agrave;nh phố Hồ Ch&iacute; Minh.</p>\r\n\r\n<p>Năm 2020, Long An l&agrave; đơn vị h&agrave;nh ch&iacute;nh&nbsp;Việt Nam&nbsp;đ&ocirc;ng thứ 13 về số d&acirc;n, trong&nbsp;danh s&aacute;ch đơn vị h&agrave;nh ch&iacute;nh Việt Nam theo GRDP, xếp thứ 10 về&nbsp;tổng sản phẩm tr&ecirc;n địa b&agrave;n (GRDP),&nbsp;xếp thứ 10&nbsp;về&nbsp;GRDP&nbsp;b&igrave;nh qu&acirc;n đầu người,&nbsp;đứng thứ 11&nbsp;về tốc độ tăng trưởng&nbsp;GRDP. Với 1.763.754 người d&acirc;n GRDP đạt 170.500 tỉ&nbsp;đồng&nbsp;(tương ứng với 7,51 tỉ USD), GRDP b&igrave;nh qu&acirc;n đầu người đạt 85 triệu đồng (tương ứng với 3748 USD), tốc độ tăng trưởng GRDP đạt 9,41%.</p>\r\n\r\n<p>Tỉnh lỵ của Long An l&agrave; th&agrave;nh phố&nbsp;T&acirc;n An, c&aacute;ch trung t&acirc;m&nbsp;Th&agrave;nh phố Hồ Ch&iacute; Minh&nbsp;khoảng 45&nbsp;km theo đường&nbsp;QL1.</p>\r\n\r\n<p>Long An l&agrave; tỉnh nằm trong&nbsp;v&ugrave;ng kinh tế trọng điểm ph&iacute;a Nam&nbsp;v&agrave; l&agrave; cửa ng&otilde; nối liền&nbsp;Đ&ocirc;ng Nam Bộ&nbsp;với khu vực&nbsp;đồng bằng s&ocirc;ng Cửu Long, nhất l&agrave; c&oacute; chung đường ranh giới với&nbsp;th&agrave;nh phố Hồ Ch&iacute; Minh, bằng hệ thống c&aacute;c quốc lộ:&nbsp;1,&nbsp;50,&nbsp;62,&nbsp;N1,&nbsp;N2&nbsp;(Đường Hồ Ch&iacute; Minh). Tỉnh được xem l&agrave; thị trường ti&ecirc;u thụ h&agrave;ng h&oacute;a n&ocirc;ng sản lớn nhất của Đồng bằng S&ocirc;ng Cửu Long.</p>\r\n\r\n<p><!-- <p><strong>Mô tả</strong></p> --></p>\r\n', ''),
(7, 'Đồng Tháp', '<p><strong>Đồng Th&aacute;p</strong>&nbsp;l&agrave; một tỉnh thuộc v&ugrave;ng&nbsp;Đồng bằng s&ocirc;ng Cửu Long,&nbsp;Việt Nam.</p>\r\n\r\n<p>V&ugrave;ng đất Đồng Th&aacute;p đ&atilde; được&nbsp;Ch&uacute;a Nguyễn&nbsp;khai ph&aacute; v&agrave;o khoảng&nbsp;thế kỷ XVII,&nbsp;XVIII. Tỉnh Đồng Th&aacute;p được th&agrave;nh lập tr&ecirc;n cơ sở hợp nhất tỉnh&nbsp;Kiến Phong&nbsp;v&agrave;&nbsp;tỉnh Sa Đ&eacute;c&nbsp;v&agrave;o năm&nbsp;1976.</p>\r\n\r\n<p>Năm&nbsp;2018, Đồng Th&aacute;p l&agrave; đơn vị h&agrave;nh ch&iacute;nh&nbsp;Việt Nam&nbsp;đ&ocirc;ng thứ 15 về số d&acirc;n,&nbsp;xếp thứ 30&nbsp;về&nbsp;Tổng sản phẩm tr&ecirc;n địa b&agrave;n (GRDP),&nbsp;xếp thứ 43&nbsp;về&nbsp;GRDP&nbsp;b&igrave;nh qu&acirc;n đầu người,&nbsp;đứng thứ 57&nbsp;về tốc độ tăng trưởng&nbsp;GRDP. Với 1.693.300 người d&acirc;n, GRDP đạt 67.732 tỉ&nbsp;Đồng&nbsp;(tương ứng với 2,9416 tỉ USD), GRDP b&igrave;nh qu&acirc;n đầu người đạt 40,00 triệu đồng (tương ứng với 1.737 USD), tốc độ tăng trưởng GRDP đạt 6,92%.</p>\r\n\r\n<p>Tỉnh Đồng Th&aacute;p l&agrave; nơi&nbsp;s&ocirc;ng Tiền&nbsp;chảy v&agrave;o địa phận Việt Nam, c&oacute; đường bi&ecirc;n giới gi&aacute;p với&nbsp;Campuchia&nbsp;c&oacute;&nbsp;chiều d&agrave;i&nbsp;hơn 50&nbsp;km với 4&nbsp;cửa khẩu,&nbsp;trong đ&oacute; c&oacute; 2 cửa khẩu quốc tế l&agrave;&nbsp;Thường Phước&nbsp;v&agrave;&nbsp;Dinh B&agrave;. Đồng Th&aacute;p nổi tiếng với những ruộng sen, hiện diện khắp nơi ở Đồng Th&aacute;p. Ng&oacute; v&agrave; hạt sen trở th&agrave;nh đặc sản của v&ugrave;ng n&agrave;y. Ngo&agrave;i ra, Đồng Th&aacute;p rất th&iacute;ch hợp cho loại h&igrave;nh&nbsp;du lịch sinh th&aacute;i.</p>\r\n\r\n<p><!-- <p><strong>Mô tả</strong></p> --></p>\r\n', ''),
(8, 'An Giang', '<p><strong>An Giang</strong>&nbsp;l&agrave; một&nbsp;tỉnh&nbsp;thuộc v&ugrave;ng&nbsp;đồng bằng s&ocirc;ng Cửu Long,&nbsp;Việt Nam.</p>\r\n\r\n<p>An Giang l&agrave; tỉnh thuộc&nbsp;V&ugrave;ng kinh tế trọng điểm v&ugrave;ng đồng bằng s&ocirc;ng Cửu Long. L&agrave; tỉnh c&oacute; d&acirc;n số đ&ocirc;ng nhất v&ugrave;ng&nbsp;đồng bằng s&ocirc;ng Cửu Long&nbsp;v&agrave; l&agrave; tỉnh đứng thứ 8 cả nước về d&acirc;n số. Một phần của An Giang nằm trong tứ gi&aacute;c Long Xuy&ecirc;n.</p>\r\n\r\n<p><!-- <p><strong>Mô tả</strong></p> --></p>\r\n', ''),
(9, 'Tiền Giang', '<p><strong>Tiền Giang</strong>&nbsp;l&agrave; một&nbsp;tỉnh&nbsp;ven biển thuộc&nbsp;đồng bằng s&ocirc;ng Cửu Long,&nbsp;Việt Nam. T&ecirc;n gọi cũ của tỉnh trước năm 1976 l&agrave;&nbsp;<strong>Định Tường.</strong></p>\r\n\r\n<p>Phần lớn diện t&iacute;ch của tỉnh thuộc địa b&agrave;n&nbsp;tỉnh Mỹ Tho&nbsp;cũ. Tuy nhi&ecirc;n, cũng c&oacute; thời kỳ to&agrave;n bộ diện t&iacute;ch tỉnh Tiền Giang ng&agrave;y nay đều thuộc tỉnh Mỹ Tho, bao gồm cả v&ugrave;ng&nbsp;G&ograve; C&ocirc;ng. Tiền Giang l&agrave; tỉnh thuộc V&ugrave;ng đồng bằng s&ocirc;ng Cửu Long, v&agrave; nằm trong&nbsp;V&ugrave;ng kinh tế trọng điểm ph&iacute;a Nam. Tỉnh lỵ của tỉnh hiện nay l&agrave;&nbsp;Th&agrave;nh phố Mỹ Tho, nằm c&aacute;ch&nbsp;Th&agrave;nh phố Hồ Ch&iacute; Minh&nbsp;70&nbsp;km về hướng t&acirc;y nam v&agrave; c&aacute;ch&nbsp;Th&agrave;nh phố Cần Thơ&nbsp;90&nbsp;km về hướng đ&ocirc;ng bắc&nbsp;theo đường&nbsp;Quốc lộ 1A.</p>\r\n\r\n<p>Tiền Giang c&oacute; đường bờ biển d&agrave;i 32&nbsp;km với địa h&igrave;nh tương đối bằng phẳng, đất ph&ugrave; sa trung t&iacute;nh, &iacute;t chua dọc&nbsp;s&ocirc;ng Tiền, chiếm khoảng 53% diện t&iacute;ch to&agrave;n tỉnh, th&iacute;ch hợp cho nhiều loại c&acirc;y trồng v&agrave; vật nu&ocirc;i. Mạng lưới viễn th&ocirc;ng Tiền Giang đ&atilde; được hiện đại h&oacute;a v&agrave; triển khai đồng loạt trong to&agrave;n tỉnh, đảm bảo th&ocirc;ng tin li&ecirc;n lạc được th&ocirc;ng suốt trong nước v&agrave;&nbsp;quốc tế. Ngo&agrave;i ra, Tiền Giang cũng l&agrave; tỉnh c&oacute; nhiều tiềm năng về du lịch.</p>\r\n\r\n<p><!-- <p><strong>Mô tả</strong></p> --></p>\r\n', ''),
(11, 'Bắc Ninh', '<p><strong>Bắc Ninh</strong>&nbsp;l&agrave; một&nbsp;tỉnh&nbsp;ở&nbsp;Việt Nam. Đ&acirc;y l&agrave; tỉnh c&oacute; diện t&iacute;ch nhỏ nhất đất nước, với vị tr&iacute; nằm trong&nbsp;V&ugrave;ng thủ đ&ocirc; H&agrave; Nội, thuộc v&ugrave;ng&nbsp;Đồng bằng s&ocirc;ng Hồng. Tỉnh cũng nằm trong&nbsp;V&ugrave;ng kinh tế trọng điểm Bắc bộ,&nbsp;Việt Nam.<!-- <p><strong>Mô tả</strong></p> --></p>\r\n\r\n<p>&nbsp;</p>\r\n', ''),
(12, 'Đắk Nông', '<p><strong>Đắk N&ocirc;ng</strong>&nbsp;l&agrave; một&nbsp;tỉnh&nbsp;thuộc v&ugrave;ng&nbsp;T&acirc;y Nguy&ecirc;n,&nbsp;miền Trung,&nbsp;Việt Nam.<!--{C}%3C!%2D%2D%20%3Cp%3E%3Cstrong%3EM%C3%B4%20t%E1%BA%A3%3C%2Fstrong%3E%3C%2Fp%3E%20%2D%2D%3E--></p>\r\n\r\n<p>Đắk N&ocirc;ng&nbsp;nằm ở cửa ng&otilde;&nbsp;ph&iacute;a t&acirc;y nam&nbsp;của&nbsp;T&acirc;y Nguy&ecirc;n, nằm trong v&ugrave;ng&nbsp;tọa độ&nbsp;từ 11&deg;45 đến 12&deg;50&nbsp;vĩ độ bắc&nbsp;v&agrave; từ 107&deg;12 đến 108&deg;07&nbsp;kinh độ đ&ocirc;ng. Trung t&acirc;m&nbsp;tỉnh Đắk N&ocirc;ng&nbsp;l&agrave; th&agrave;nh phố Gia Nghĩa nằm c&aacute;ch&nbsp;th&agrave;nh phố Bu&ocirc;n Ma Thuột&nbsp;với&nbsp;chiều d&agrave;i&nbsp;125&nbsp;km theo đường&nbsp;quốc lộ 14, c&aacute;ch&nbsp;th&agrave;nh phố Hồ Ch&iacute; Minh&nbsp;khoảng 250&nbsp;km về ph&iacute;a Nam, c&aacute;ch th&agrave;nh phố&nbsp;Đ&agrave; Nẵng&nbsp;689&nbsp;km về ph&iacute;a Bắc, c&aacute;ch Thủ đ&ocirc;&nbsp;H&agrave; Nội&nbsp;1358&nbsp;km về ph&iacute;a Bắc</p>\r\n\r\n<p>N&eacute;t đặc sắc của Đắk N&ocirc;ng c&oacute; lẽ vẫn phần n&agrave;o l&agrave; rất nhiều của Đắk Lắk do một thời gian d&agrave;i tỉnh n&agrave;y l&agrave; một khu vực của Đắk Lắk.</p>\r\n\r\n<p>V&ugrave;ng đất n&agrave;y c&oacute; nền văn h&oacute;a cổ truyền kh&aacute; đa dạng của nhiều d&acirc;n tộc đậm n&eacute;t truyền thống v&agrave; bản sắc ri&ecirc;ng. Nơi đ&acirc;y c&ograve;n lưu giữ nhiều pho sử thi truyền miệng rất độc đ&aacute;o như&nbsp;sử thi&nbsp;<em>Đam San</em>&nbsp;d&agrave;i h&agrave;ng ng&agrave;n c&acirc;u. C&aacute;c luật tục cổ, kiến tr&uacute;c&nbsp;nh&agrave; s&agrave;n,&nbsp;nh&agrave; r&ocirc;ng&nbsp;v&agrave; tượng nh&agrave; mồ c&ograve;n chứa bao điều b&iacute; ẩn đầy hấp dẫn.</p>\r\n\r\n<p>C&aacute;c loại nhạc cụ d&acirc;n tộc từ l&acirc;u đời đ&atilde; trở n&ecirc;n nổi tiếng v&agrave; l&agrave; niềm tự h&agrave;o của cả v&ugrave;ng&nbsp;T&acirc;y Nguy&ecirc;n, của văn h&oacute;a d&acirc;n gian Việt Nam: bộ đ&agrave;n đ&aacute; của người M&#39;N&ocirc;n', 'Array');

